'use strict';

module.exports = args;

var debug = require('debug')('clite');
var minimist = require('minimist');
var abbrev = require('abbrev');
var getSettings = require('./settings');
var defaultsDeep = require('lodash.defaultsdeep');

function args(argv, config) {
  var settings = getSettings(config);
  if (!Array.isArray(argv)) {
    throw new Error('process.argv type expected');
  }

  defaultsDeep(settings.alias, abbrev(settings.options.concat(settings.booleans)));

  // fixes up any issue
  settings.options.forEach(function (t) {
    return delete settings.alias[t];
  });

  debug('config', config);

  var res = minimist(argv, {
    string: settings.options,
    alias: settings.alias,
    boolean: settings.booleans
  });
  res.argv = res._.slice(0);
  res._.splice(0, 2); // now remove the `node` + script name

  debug('args', res);

  var override = false;

  if (res.help) {
    override = 'help';
    if (res._.length) {
      res.help = res._.pop();
    }
  }

  if (res.version) {
    override = 'version';
  }

  res.command = settings.commands[override || res._[0]] || settings.commands._ || '.'; // aka index

  // if our command was used off the CLI arg, then let's remove it from the
  // command args
  if (res.command === settings.commands[res._[0]]) {
    res._.shift();
  }

  return res;
}